import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Container } from 'react-bootstrap'
import HomeCard from '../HomeCard'


const LandingPage = () =>{

    const dispatch = useDispatch()
    const lHomes = useSelector(state => state.homes)

    useEffect(() => {
        fetch('http://localhost:3000/api/v1/homes')
        .then(res => res.json())
        .then(homes => {
            dispatch({
                type: 'SET_ALL_HOMES',
                homes: homes
            })
        }
    )}, [dispatch])


        return (
            <>
                 <BannerPic />
                <main className='py-4'>
                    <br /><br />
                    <Container>
                        <h3 className='navHome' style={{textAlign: "center"}}>START PLANNING YOUR NEXT ADVENTURE</h3>
                    </Container>
                    <br /><br />
                    <Row>
                    {lHomes.map(home => (
                        <Col key={home._id} sm={14} md={12} lg={10} xl={8} >
                            <HomeCard home={home} />
                        </Col>
                    ))}
                    </Row>  
            </main>
            </>
        )

}

export default LandingPage
