import { 
    HOME_LIST_REQUEST,
    HOME_LIST_SUCCESS,
    HOME_LIST_FAIL,
    HOME_DETAILS_REQUEST,
    HOME_DETAILS_SUCCESS,
    HOME_DETAILS_FAIL
} from '../constants/homeConstants'
import axios from 'axios'


export const listAllHomes = () =>  async (dispatch) => {
    try {
        dispatch({ type: HOME_LIST_REQUEST })

        const { data } = await axios.get('http://localhost:3000/api/v1/homes/')
        // console.log(data)
        dispatch({ type: HOME_LIST_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: HOME_LIST_FAIL,
            payload: 
                error.response && error.response.data.message 
                    ? error.response.data.message
                    : error.message,
        })
    }

    try {
        dispatch({ type: HOME_DETAILS_REQUEST })

        const { data } = await axios.get('http://localhost:3000/api/v1/homes/:id')
        // console.log(data)
        dispatch({ type: HOME_DETAILS_SUCCESS, payload: data})
    } catch (error) {
        dispatch({
            type: HOME_DETAILS_FAIL,
            payload: 
                error.response && error.response.data.message 
                    ? error.response.data.message
                    : error.message,
        })
    }
}
