import React, { useEffect} from 'react'
import { Carousel, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'

const HomeShowPage = ({ match }) => {

    const dispatch = useDispatch()
    const home = useSelector(state => state.home)


    useEffect(() => {
        fetch(`http://localhost:3000/api/v1/homes/${match.params.id}`)
        .then(res => res.json())
        .then(home => {
            dispatch({
                type: 'SET_HOME',
                home: home
            })
        }
    )})

    return (
        <>
        <h3 className='navHome'>{home.location}</h3>
        <Carousel>
            {home.photos.map(photo => (
                <Carousel.Item interval={4000}>
                    <Image
                    className="d-block w-100"
                    src={photo.image}
                    alt="home photos"
                    />
                    <Carousel.Caption>
                    <p>{photo.image_name}</p>
                    </Carousel.Caption>
                </Carousel.Item>
            ))}
        </Carousel>
        <br /><br />
        <Card style={{border:'white'}}>
            <Card.Title as='h4'>{home.description}</Card.Title>
            <Card.Body>
                <Card.Text>
                    <h6>
                        Bedrooms: {home.bedroom}
                    </h6>
                    <h6>
                        Bathrooms: {home.bathroom}
                    </h6>
                    <h6>
                        Accommodates: {home.accommodates}
                    </h6>
                    <h6>
                        Pet Friendly: {home.pets_allowed
                                    ?   yes
                                    :   no
                                    }
                    </h6>
                </Card.Text>
            </Card.Body>

            <h3><small>
                $ {home.daily_price} / night
            </small></h3>

        </Card>
        </>
    )
}

export default HomeShowPage
