import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Button, Card, Col, Image, Container, Row } from 'react-bootstrap'
import { Link } from 'react-router-dom'

const UserProfile = () => {

    const dispatch = useDispatch()
    const user = useSelector(state => state.user)
    const homes = useSelector(state => state.homes)

    function renderRes() {
        return(
            // <div>
            //     {user.reservations.map(res => (
            //         <Card>
            //             <Card.Img variant="top" src={res.home.photos[1].image} />
            //             <Card.Body>
            //                 <Card.Title>
            //                 {res.home.location}
            //                 </Card.Title>
            //                 <Card.Text>
            //                     Reserved for {res.start_date} - {res.end_date}
            //                 </Card.Text>
            //             <Button >Cancel Reservation</Button>
            //             </Card.Body>
            //         </Card>
            //     ))}
            // </div>
            <Card>
                <Card.Img variant="top" src={homes.last.photos[1].image} />
                <Card.Body>
                    <Card.Title>
                    {homes.last.location}
                    </Card.Title>
                    <Card.Text>
                        Reserved for 12/24/2020 - 01/3/2021
                    </Card.Text>
                <Button >Cancel Reservation</Button>
                </Card.Body>
            </Card>
        )
    }


    // function handleClick = () => {
    //     delete reservation instance on Calcel Reservation Button
    // }

    return(
        <>
            <Container style={{padding: '100px'}}>
                <Row>
                    <Col sm={3}>
                        <Card style={{border:'white'}}>
                            <Card.Body className='text-center'>
                                <Card.Title>
                                    {/* <Image src="https://cdn.pixabay.com/photo/2017/06/13/12/53/profile-2398782_1280.png" roundedCircle fluid/> */}
                                    <Image src={user.image} thumbnail fluid/>
                                </Card.Title>
                                <Card.Text>
                                    <Link to={`/users/edit`}>
                                        <Button size='sm'>Edit Account</Button>
                                    </Link>
                                </Card.Text>
                                {/* <Card.Text>
                                    <Button href='/user/list_home'>List a Home</Button>
                                </Card.Text> */}
                            </Card.Body>
                        </Card>
                    </Col>
                    <Col sm={9}>
                    <h3 className='navHome'> Upcoming Reservations</h3> <br />
                        {homes ? renderRes() : null}   



                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default UserProfile
