import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Form, Button, Card, Col, Image } from 'react-bootstrap'

const UpdateUserForm = () => {

    const dispatch = useDispatch()
    const nameInput = useSelector(state => state.nameInput)
    const imageInput = useSelector(state => state.imageInput)
    const emailInput = useSelector(state => state.emailInput)
    const usernameInput = useSelector(state => state.usernameInput)
    const passwordInput = useSelector(state => state.passwordInput)


    const handleNameChange = (e) => {
        dispatch({
            type: 'SET_NAME_INPUT',
            value: e.target.value
        })
    }

    const handleImageChange = (e) => {
        dispatch({
            type: 'SET_IMAGE_INPUT',
            value: e.target.value
        })
    }

    const handleEmailChange = (e) => {
        dispatch({
            type: 'SET_EMAIL_INPUT',
            value: e.target.value
        })
    }

    const handleUsernameChange = (e) => {
        dispatch({
            type: 'SET_USERNAME_INPUT',
            value: e.target.value
        })
    }

    const handlePasswordChange = (e) => {
        dispatch({
            type: 'SET_PASSWORD_INPUT',
            value: e.target.value
        })
    }

    const handleClick = () => {
        dispatch({
            type: 'SET_USER',
            user: {
                name: nameInput,
                image: imageInput,
                email: emailInput,
                username: usernameInput,
                password: passwordInput
            }

        })
    }

    return(
        <>
            <Col sm={3}>
                <Card>
                    <Card.Body className='text-center'>
                        <Card.Title>
                            <Image src="https://cdn.pixabay.com/photo/2017/06/13/12/53/profile-2398782_1280.png" roundedCircle style={{height:'100px', width:'100px'}}/>
                        </Card.Title>
                        <Card.Text>
                            <Button>Edit Account</Button>
                        </Card.Text>
                        <Card.Text>
                            <Button>List a Home</Button>
                        </Card.Text>
                    </Card.Body>
                </Card>
            </Col>


            <Col sm={9}>
            <h3> Upcoming Reservations</h3> <br />
            <Card>
                <Card.Img variant="top" src="https://www.boutique-homes.com/wp-content/uploads/2020/09/Riad_in_Marrakech_003.jpg" />
                <Card.Body>
                <Card.Text>
                    'home.location'
                </Card.Text>
                <Card.Text>
                    Reserved for 'reservation.start_date' - 'reseravtion.end_date'
                </Card.Text>
                <Button>Delete Reservation</Button>
                </Card.Body>
            </Card>
            <br /><br />

            <Card>
                <Card.Img variant="top" src="https://www.boutique-homes.com/wp-content/uploads/2019/05/holiday_home_portugal_023.jpg" />
                <Card.Body>
                <Card.Text>
                    'home.location'
                </Card.Text>
                <Card.Text>
                    Reserved for 'reservation.start_date' - 'reseravtion.end_date'
                </Card.Text>
                <Button>Delete Reservation</Button>
                </Card.Body>
            </Card>
            </Col>


            <br /><br />
            <hr></hr>
            <br /><br />

            <h3>Update Account Information</h3> <br />

                <Form>
                    <Form.Group >
                        {/* <Form.Label>Name</Form.Label> */}
                        <Form.Control type="name" placeholder="Enter Full Name" onChange={(e) =>handleNameChange(e)}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.File id="image" label="Upload a New Image:" onChange={(e) =>handleImageChange(e)}/>
                    </Form.Group>

                    <Form.Group >
                        {/* <Form.Label>Email</Form.Label> */}
                        <Form.Control type="email" placeholder="Enter Email" onChange={(e) =>handleEmailChange(e)}/>
                    </Form.Group>

                    <Form.Group >
                        {/* <Form.Label>Username</Form.Label> */}
                        <Form.Control type="username" placeholder="Enter Username" onChange={(e) =>handleUsernameChange(e)}/>
                    </Form.Group>

                    <Form.Group>
                        {/* <Form.Label>Password</Form.Label> */}
                        <Form.Control type="password" placeholder="Password" onChange={(e) =>handlePasswordChange(e)}/>
                    </Form.Group>

                    <Form.Group>
                        {/* <Form.Label>Password</Form.Label> */}
                        <Form.Control type="password" placeholder="Password Confirmation" onChange={(e) =>handlePasswordChange(e)}/>
                    </Form.Group>

                    <Button variant="primary" type="submit" onClick={handleClick}>
                        Save Changes
                    </Button>
                </Form>
        </>
    )
}

export default UpdateUserForm
