import { CardGroup, Card, Image, Row, Col, Jumbotron, Button } from 'react-bootstrap'
import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { LinkContainer } from 'react-router-bootstrap'


function FeaturesCards ({ home }) {

    const dispatch = useDispatch()
    const lHomes = useSelector(state => state.homes)

    useEffect(() => {
        fetch('http://localhost:3000/api/v1/homes')
        .then(res => res.json())
        .then(homes => {
            dispatch({
                type: 'SET_ALL_HOMES',
                homes: homes
            })
        }
    )}, [dispatch])


    function renderCards() {
        return (
            <>
             <CardGroup fluid>  
                  {lHomes.map(home => (
                    <Row>
                        if(home.id === 3 || home.id === 2){
                           <Card>
                                <Image src={home.photos[2].image} fluid />
                            </Card>
                        }else {
                        <p>oops</p>
                        }
                    </Row>
                  ))}
            </CardGroup>
            {/* {lHomes.map(home => (
                <CardGroup fluid>   
                    <Card class="container">
                        <Card.Img src={home.photos[2].image} />
                        <div class="text-block">
                            <h4>Feature Homes</h4>
                            <p>Click to explore more</p>
                        </div>
                    </Card>
                    <Card class="container">
                        <Card.Img src={home.photos[3].image} />
                        <div class="text-block">
                            <h4>Featured Home</h4>
                            <p>Click to explore more</p>
                        </div>
                    </Card>
                </CardGroup>
            ))} */}
            </>
    )}

    return (
        <>

            {lHomes ? renderCards() : null}


        </>
    )
}

export default FeaturesCards;
