export const initialState = {
    usernameInput: '',
    passwordInput: '',
    user: {},
    emailInput: '',
    imageInput: '',
    nameInput: '',
    homes: [],

    description: '',
    location: '',
    daily_price: 0.00,
    bedroom: 0,
    bathroom: 0,
    image: '',
    home: {}
}


export const reducer = (state, action) => {
    switch(action.type){
        case 'SET_USERNAME_INPUT':
            return {
                ...state,
                usernameInput: action.value
            }
        break;
        case 'SET_PASSWORD_INPUT':
            return {
                ...state,
                passwordInput: action.value
            }
        break;
        case 'SET_USER':
            return {
                ...state,
                user: action.user
            }
        break;
        case 'SET_NAME_INPUT':
            return {
                ...state,
                nameInput: action.value
            }
        break;
        case 'SET_IMAGE_INPUT':
            return {
                ...state,
                imageInput: action.image
            }
        break;
        case 'SET_EMAIL_INPUT':
            return {
                ...state,
                emailInput: action.value
            }
        break;
        case 'SET_ALL_HOMES':
            return {
                ...state,
                homes: action.homes
            }
        break;

    }
    return state
}
